// import { React, useEffect, useState } from "react"; 
import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const BookList = () => {
    const [books, setBooks] = useState([]);

    // const getBooks = async () => { 
    //   const response = await axios.get("http://localhost:5000/products"); 
    //   setBooks(response.data); 
    // }; 
    const getBooks = async () => {
        try {
            const { data, status } = await axios.get(
                "http://localhost:5000/products/",
                {}
            );
            if (status === 200) {
                setBooks(data);
            }
        } catch (err) {
            console.log(err);
        }
    };

    const deleteBooks = async (id) => {
        await axios.delete(`http://localhost:5000/products/${id}`);
        getBooks();
    };
    useEffect(() => {
        getBooks();
    }, []);

    return (
        <div>
            {/* Button add */}
            <Link to="/add" className="btn btn-primary">
                Add Books
            </Link>
            {/* Card book */}
            {books.map((book, index) => (
                <body key={index}>
                    <div class="card col-md-3" key={book.id}>
                        <img src={book.coverImage} class="card-img-top" alt=" " />
                        <div class="card-body">
                            <h5 class="card-title">{book.title}</h5>
                            <p class="card-text">{book.author}</p>
                            <p class="card-text">{book.deskripsi}</p>
                            <div className="d-flex">
                                <p class="card-text">{book.stok}</p>
                                <p class="card-text">{book.price}</p>
                            </div>
                            <button class="btn btn-primary"
                                onClick={() => deleteBooks(book.id)}
                                className="button is-small is-danger">Delete</button>
                            <button to={`/edit/${book.id}`}
                                className="btn btn-primary">Edit</button>
                        </div>
                    </div>
                </body>
            ))}
        </div>
    );
};

export default BookList;
