// import { React, useEffect, useState } from "react"; 
import { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const BookList = () => {
    const [books, setBooks] = useState([]);

    // const getBooks = async () => { 
    //   const response = await axios.get("http://localhost:5000/products"); 
    //   setBooks(response.data); 
    // }; 
    const getBooks = async () => {
        try {
            const { data, status } = await axios.get(
                "http://localhost:5000/products/",
                {}
            );
            if (status === 200) {
                setBooks(data);
            }
        } catch (err) {
            console.log(err);
        }
    };

    const deleteBooks = async (id) => {
        await axios.delete(`http://localhost:5000/products/${id}`);
        getBooks();
    };
    useEffect(() => {
        getBooks();
    }, []);

    return (
        <div>
            {/* Navbar */}
            <nav class="navbar navbar-expand-lg bg-light">
                <div class="container-fluid">
                    <a class="navbar-brand" href="#">Navbar</a>
                    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                        <span class="navbar-toggler-icon"></span>
                    </button>
                    <div class="collapse navbar-collapse" id="navbarSupportedContent">
                        <ul class="navbar-nav me-auto mb-2 mb-lg-0">
                            <li class="nav-item">
                                <a class="nav-link active" aria-current="page" href="#">Home</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link" href="#">Link</a>
                            </li>
                            <li class="nav-item dropdown">
                                <a class="nav-link dropdown-toggle" href="#" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                    Dropdown
                                </a>
                                <ul class="dropdown-menu">
                                    <li><a class="dropdown-item" href="#">Action</a></li>
                                    <li><a class="dropdown-item" href="#">Another action</a></li>
                                    <li><hr class="dropdown-divider" /></li>
                                    <li><a class="dropdown-item" href="#">Something else here</a></li>
                                </ul>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link disabled">Disabled</a>
                            </li>
                        </ul>
                        <form class="d-flex" role="Account">
                            <li>
                                <a class="nav-link"><i class="bi bi-cart3"></i></a>
                            </li>
                        </form>
                    </div>
                </div>
            </nav>
            {/* Button add */}
            <Link to="/add">
                <button className="btn btn-primary text-center">
                    Add Books
                </button>
            </Link>
            {/* Card book */}
            {books.map((book, index) => (
                <body key={index}>
                    <div class="card col-md-3" key={book.id}>
                        <img src={book.coverImage} class="card-img-top" alt=" " />
                        <div class="card-body">
                            <h5 class="card-title">{book.title}</h5>
                            <p class="card-text">{book.author}</p>
                            <p class="card-text">{book.deskripsi}</p>
                            <button class="btn btn-primary"
                                onClick={() => deleteBooks(book.id)}
                                className="button is-small is-danger">Delete</button>
                            <button to={`/edit/${book.id}`}
                                className="btn btn-primary">Edit</button>
                        </div>
                    </div>
                </body>
            ))}
        </div>
    );
};

export default BookList;
