import { useState } from 'react'
import axios from "axios";
import { useNavigate } from 'react-router-dom';

const AddBook = () => {
    const [title, setTitle] = useState(' ');
    const [coverImage, setCoverImage] = useState(' ');
    const [author, setAuthor] = useState(' ');
    const [deskripsi, setDeskripsi] = useState(' ');
    const [stok, setStok] = useState(' ');
    const [price, setPrice] = useState(' ');
    const navigate = useNavigate();

    const saveBook = async (e) => {
        e.preventDefault();
        await axios.post('http://localhost:5000/products', {
            title: title,
            coverImage: coverImage,
            author: author,
            deskripsi: deskripsi,
            stok: stok,
            price: price
        });
        navigate.push("/");
    }

    return (
        <div>
            <div onSubmit={saveBook} className="text-center">
                <div className="field">
                    <label className="label mt-5">Title: </label>
                    <input
                        className="input mt-5"
                        type="text"
                        placeholder="Title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                    />
                </div>

                <div className="field">
                    <label className="label mt-5">Cover image: </label>
                    <input
                        className="input mt-5"
                        type="text"
                        placeholder="cover image"
                        value={coverImage}
                        onChange={(e) => setCoverImage(e.target.value)}
                    />
                </div>

                <div className="field">
                    <label className="label mt-5">Author: </label>
                    <input
                        className="input mt-5"
                        type="text"
                        placeholder="author book"
                        value={author}
                        onChange={(e) => setAuthor(e.target.value)}
                    />
                </div>

                <div className="field">
                    <label className="label mt-5">Deskripsi: </label>
                    <input
                        className="input mt-5"
                        type="text"
                        placeholder="Deskripsi"
                        value={deskripsi}
                        onChange={(e) => setDeskripsi(e.target.value)}
                    />
                </div>

                <div className="field">
                    <label className="label mt-5">Stok: </label>
                    <input
                        className="input mt-5"
                        type="text"
                        placeholder="stok"
                        value={stok}
                        onChange={(e) => setStok(e.target.value)}
                    />
                </div>

                <div className="field">
                    <label className="label mt-5">Price: </label>
                    <input
                        className="input mt-5"
                        type="text"
                        placeholder="price"
                        value={price}
                        onChange={(e) => setStok(e.target.value)}
                    />
                </div>

                <button className="btn btn-success">Save</button>
            </div>
        </div>
    )
}

export default AddBook
